import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import Layout from "./Layout";
import { Link } from "react-router-dom";

const Userpage = () => {
  const [listPosts, setPosts] = useState([]);
  const [display, setDisplay] = useState(false);
  const [inpopup, setinpopup] = useState("");
  const [user, setUser] = useState([]);
  const [popUpdetails, setpopUpdetails] = useState({
    path: "",
    name: "",
    email: "",
  });

  useEffect(() => {
    axios
      .get("http://localhost:8000/blogs/all-posts")
      .then((res) => {
        console.log("fetching posts from server SUCCESS", res);

        setPosts(res.data.result);
        console.log(listPosts);
      })
      .catch((err) => {
        console.log("Error in fetching from database", err);
      });
  }, []);

  const myFunction = (email) => () => {
    console.log("calling my func ==>" + email);

    axios
      .post("http://localhost:8000/blogs/get-user-details", {
        email,
      })
      .then((res) => {
        console.log("Success in Fetching user detail", email, res);
        let tempPath = String(res.data.result.profileImgPath);
        tempPath = "http://localhost:8000/uploads/" + tempPath.substr(15);
        setpopUpdetails({
          ...popUpdetails,
          name: res.data.result.name,
          path: tempPath,
          email: res.data.result.email,
        });
        // setUser(res.data.result);
      })
      .catch((err) => {
        if (err) {
          console.log(
            "Error in fetching user details from the user",
            email,
            err
          );
        }
      });

    setinpopup(email);
    var modal = document.getElementById("myModal");
    modal.style.display = "block";
  };

  const closePopup = () => {
    console.log("calling closePopup");
    var modal = document.getElementById("myModal");
    modal.style.display = "none";
  };
  return (
    <Layout>
      <div id="myModal" class="modal">
        <div class="modal-content">
          <span class="close" onClick={closePopup}>
            &times;
          </span>
          <span id="email">{inpopup}</span>
        </div>
        <div>
          <div className="container mt-5 custom-top">
            <div>
              <div className="card">
                <div className="row">
                  <div className="col-md-6">
                    {" "}
                    <img
                      className="card-img-top img-fluid"
                      src={popUpdetails.path}
                    />
                  </div>
                  <div className="col-md-6">
                    <h4
                      className="card-title"
                      onClick={() => console.log(`cliclk `)}
                    >
                      {popUpdetails.name}
                    </h4>
                    <p className="card-text">{email}</p>
                    <p>{popUpdetails.path}</p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      <h1>Blog Posts</h1>
      <div className="nav">
        {" "}
        <li>
          <Link to="/add-new-article" className="navbar-left">
            Add New Article
          </Link>{" "}
        </li>
      </div>
      <div>
        {listPosts.map((post, index) => {
          const path =
            "http://localhost:8000/uploads/" + post.imagePath.substr(15);
          const detailPath = "/user/dashboard/showpopup/" + post.postedByEmail;
          return (
            <div className="card mt-4 custom-article-form" key={post._id}>
              <div className="row no-gutters">
                <div className="col-md-4">
                  <img src={path} className="card-img img-fluid" alt="lol" />
                </div>
                <div className="col-md-8">
                  <div className="card-title">{post.title}</div>
                  <div className="card-body">
                    {post.description}
                    <p>{post.markdown}</p>
                    <p>
                      <button onClick={myFunction(post.postedByEmail)}>
                        Click me
                      </button>
                      posted by: <Link to={detailPath}>{post.author}</Link>
                    </p>
                  </div>
                </div>
              </div>
            </div>
          );
        })}
      </div>
    </Layout>
  );
};

export default Userpage;
