import React, { Fragment } from "react";
import { Link, withRouter } from "react-router-dom";
import { isAuth, signout } from "../utils/helper";
import { useDispatch } from "react-redux";
import {
  Nav,
  DropdownButton,
  Dropdown,
  NavDropdown,
  Navbar,
  NavbarBrand,
} from "react-bootstrap";

const Layout = ({ children, match, history }) => {
  const dispatch = useDispatch();
  const isActive = (path) => {
    if (match.path === path) {
      return { color: "#F0E68C" };
    } else {
      return { color: "#00FFFF" };
    }
  };

  const nav = () => (
    <Navbar className="custom-navbar">
      <Navbar.Brand href="/"></Navbar.Brand>
      <Navbar.Toggle aria-controls="basic-navbar-nav" />

      <Navbar.Collapse id="basic-navbar-nav">
        <Nav className="mr-auto">
          {!isAuth() && <Nav.Link href="/signup">Signup</Nav.Link>}
          {isAuth() && <Nav.Link href="/user/dashboard">Dashboard</Nav.Link>}
          {isAuth() && (
            <Nav.Link
              onClick={() => {
                signout(() => {
                  history.push("/");
                  localStorage.clear();
                });
              }}
            >
              Signout
            </Nav.Link>
          )}
          <NavDropdown title="View Profile" className="mr-md-5">
            <NavDropdown.Item href="/user/posts">My Posts</NavDropdown.Item>
            <NavDropdown.Item href="/user/update-profile">
              Profile
            </NavDropdown.Item>
          </NavDropdown>
        </Nav>
      </Navbar.Collapse>
    </Navbar>
  );

  return (
    <Fragment>
      {nav()}
      <div className="container">{children}</div>
    </Fragment>
  );
};

export default withRouter(Layout);
