import React, { useState, useEffect } from "react";
import axios from "axios";
import Layout from "./Layout";
import { Link } from "react-router-dom";
import PopUp from "./PopUp";

const Userpage = () => {
  const [listPosts, setPosts] = useState([]);
  const [displayPopup, setdisplaypopup] = useState(false);

  useEffect(() => {
    axios
      .get("http://localhost:8000/blogs/all-posts")
      .then((res) => {
        console.log("fetching posts from server SUCCESS", res);

        setPosts(res.data.result);
        console.log(listPosts);
      })
      .catch((err) => {
        console.log("Error in fetching from database", err);
      });
  }, []);

  const togglePopup = () => {
    setdisplaypopup(!displayPopup);
  };
  const PopUp = (props) => {
    const [user, setUser] = useState([]);
    const email = props.email;
    useEffect(() => {
      Axios.post("http://localhost:8000/blogs/get-user-details", {
        email,
      })
        .then((res) => {
          console.log("Success in Fetching user detail", res);

          setUser(res.data.result);
        })
        .catch((err) => {
          if (err) {
            console.log(
              "Error in fetching user details from the user",
              email,
              err
            );
          }
        });
    }, []);

    const handleClick = () => {
      props.toggle();
    };

    return (
      <div className="modal">
        <div className="modal-content">
          <span className="close" onClick={handleClick}>
            &times;
          </span>
          <div className="card">
            <img className="card-img-top" alt="Card image" />
            <div className="card-body">
              <h4 className="card-title">{user.name}</h4>
              <p className="card-text">{email}</p>
            </div>
          </div>
        </div>
      </div>
    );
  };

  return (
    <Layout>
      <h1>Blog Posts</h1>
      <div className="nav">
        {" "}
        <li>
          <Link to="/add-new-article" className="navbar-left">
            Add New Article
          </Link>{" "}
        </li>
      </div>
      <div>
        {listPosts.map((post) => {
          const path =
            "http://localhost:8000/uploads/" + post.imagePath.substr(15);
          return (
            <div className="card mt-4 custom-article-form" key={post._id}>
              <div className="row no-gutters">
                <div className="col-md-4">
                  <img src={path} className="card-img img-fluid" alt="lol" />
                </div>
                <div className="col-md-8">
                  <div className="card-title">{post.title}</div>
                  <div className="card-body">
                    {post.description}
                    <p>{post.markdown}</p>
                    <p onClick={togglePopup}>posted by: {post.author}</p>
                    {displayPopup ? (
                      <PopUp toggle={togglePopup} email={post.postedByEmail} />
                    ) : null}
                  </div>
                </div>
              </div>
            </div>
          );
        })}
      </div>
    </Layout>
  );
};

export default Userpage;
