import React, { Fragment } from "react";
import { Link, withRouter } from "react-router-dom";
import { isAuth, signout } from "../utils/helper";
import { useDispatch } from "react-redux";
import {
  DropdownButton,
  Dropdown,
  NavDropdown,
  Navbar,
  NavbarBrand,
} from "react-bootstrap";

const Layout = ({ children, match, history }) => {
  const dispatch = useDispatch();
  const isActive = (path) => {
    if (match.path === path) {
      return { color: "#F0E68C" };
    } else {
      return { color: "#00FFFF" };
    }
  };

  const nav = () => (
    <Navbar className="custom-navbar">
      <div className="container-fluid">
        <Navbar.Brand href="/"></Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />

        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="mr-auto">
            <Nav.Link href="#home">Home</Nav.Link>
            <Nav.Link href="#link">Link</Nav.Link>
            <NavDropdown title="Dropdown" id="basic-nav-dropdown">
              <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
              <NavDropdown.Item href="#action/3.2">
                Another action
              </NavDropdown.Item>
              <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
              <NavDropdown.Divider />
              <NavDropdown.Item href="#action/3.4">
                Separated link
              </NavDropdown.Item>
            </NavDropdown>
          </Nav>
          <Form inline>
            <FormControl type="text" placeholder="Search" className="mr-sm-2" />
            <Button variant="outline-success">Search</Button>
          </Form>
        </Navbar.Collapse>
        {!isAuth() && (
          <>
            <li style={isActive("/signup")}>
              <a href="/signup"> Signup</a>
            </li>
          </>
        )}

        {isAuth() && (
          <>
            <li style={isActive("/user/dashboard")}>
              <a href="/user/dashboard"> Dashboard</a>
            </li>

            <ul className="navbar-nav ml-auto" style={{ display: "inline" }}>
              <li
                style={{ cursor: "pointer", color: "#fff" }}
                onClick={() => {
                  signout(() => {
                    history.push("/");
                    localStorage.clear();
                  });
                }}
              >
                Signout
              </li>

              <li>
                <NavDropdown title="View Profile">
                  <NavDropdown.Item href="/user/posts">
                    My Posts
                  </NavDropdown.Item>
                  <NavDropdown.Item href="/user/update-profile">
                    Profile
                  </NavDropdown.Item>
                </NavDropdown>
              </li>
            </ul>
          </>
        )}
      </div>
    </Navbar>
  );

  return (
    <Fragment>
      {nav()}
      <div className="container">{children}</div>
    </Fragment>
  );
};

export default withRouter(Layout);
