import React, { Fragment } from "react";
import { Link, withRouter } from "react-router-dom";
import { isAuth, signout } from "../utils/helper";
import { useDispatch } from "react-redux";
import {
  DropdownButton,
  Dropdown,
  NavDropdown,
  Navbar,
  NavbarBrand,
} from "react-bootstrap";

const Layout = ({ children, match, history }) => {
  const dispatch = useDispatch();
  const isActive = (path) => {
    if (match.path === path) {
      return { color: "#F0E68C" };
    } else {
      return { color: "#00FFFF" };
    }
  };

  const nav = () => (
    <Navbar className="custom-navbar">
      <div className="container-fluid">
        <Navbar.Brand href="/"></Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        {!isAuth() && (
          <>
            <li style={isActive("/signup")}>
              <a href="/signup"> Signup</a>
            </li>
          </>
        )}

        {isAuth() && (
          <>
            <li style={isActive("/user/dashboard")}>
              <a href="/user/dashboard"> Dashboard</a>
            </li>

            <ul className="navbar-nav ml-auto" style={{ display: "inline" }}>
              <li
                style={{ cursor: "pointer", color: "#fff" }}
                onClick={() => {
                  signout(() => {
                    history.push("/");
                    localStorage.clear();
                  });
                }}
              >
                Signout
              </li>

              <li>
                <NavDropdown title="View Profile">
                  <NavDropdown.Item href="/user/posts">
                    My Posts
                  </NavDropdown.Item>
                  <NavDropdown.Item href="/user/update-profile">
                    Profile
                  </NavDropdown.Item>
                </NavDropdown>
              </li>
            </ul>
          </>
        )}
      </div>
    </Navbar>
  );

  return (
    <Fragment>
      {nav()}
      <div className="container">{children}</div>
    </Fragment>
  );
};

export default withRouter(Layout);
