import React, { Fragment } from "react";
import { Link, withRouter } from "react-router-dom";
import { isAuth, signout } from "../utils/helper";
import { useDispatch } from "react-redux";
import { DropdownButton, Dropdown, NavDropdown } from "react-bootstrap";

const Layout = ({ children, match, history }) => {
  const dispatch = useDispatch();
  const isActive = (path) => {
    if (match.path === path) {
      return { color: "#F0E68C" };
    } else {
      return { color: "#00FFFF" };
    }
  };

  const nav = () => (
    <nav className="navbar custom-navbar">
      <div className="container-fluid">
        <div className="navbar-header">
          <a className="navbar-brand" href="/">
            Traveller
          </a>
        </div>

        {!isAuth() && (
          <>
            <li style={isActive("/signup")}>
              <a href="/signup"> Signup</a>
            </li>
          </>
        )}

        {isAuth() && (
          <>
            <li style={isActive("/user/dashboard")}>
              <a href="/user/dashboard"> Dashboard</a>
            </li>

            <ul className="navbar-nav ml-auto" style={{ display: "inline" }}>
              <li
                style={{ cursor: "pointer", color: "#fff" }}
                onClick={() => {
                  signout(() => {
                    history.push("/");
                    localStorage.clear();
                  });
                }}
              >
                Signout
              </li>

              <li>
                <NavDropdown title="View Profile">
                  <NavDropdown.Item href="/user/posts">
                    My Posts
                  </NavDropdown.Item>
                  <NavDropdown.Item href="/user/update-profile">
                    Profile
                  </NavDropdown.Item>
                </NavDropdown>
              </li>
            </ul>
          </>
        )}
      </div>
    </nav>
  );

  return (
    <Fragment>
      {nav()}
      <div className="container">{children}</div>
    </Fragment>
  );
};

export default withRouter(Layout);
