import React, { useEffect, useState } from "react";
import Axios from "axios";

const PopUp = ({ match }) => {
  const [user, setUser] = useState([]);

  const [email, setEmail] = useState(match.params.userToken);
  useEffect(() => {
    Axios.post("http://localhost:8000/blogs/get-user-details", {
      email,
    })
      .then((res) => {
        console.log("Success in Fetching user detail", email, res);
        setUser(res.data.result);
      })
      .catch((err) => {
        if (err) {
          console.log(
            "Error in fetching user details from the user",
            email,
            err
          );
        }
      });
  }, []);
  let tempPath = String(user.profileImgPath);
  tempPath = "http://localhost:8000/uploads/" + tempPath.substr(15);

  // const handleClick = () => {
  //   props.toggle();
  // };

  return (
    <div className="container mt-5 custom-top">
      <div>
        <span className="custom-close">&times;</span>
        <div className="card">
          <div className="row">
            <div className="col-md-6">
              {" "}
              <img className="card-img-top img-fluid" src={tempPath} />
            </div>
          </div>

          <div className=" col-md-6">
            <h4
              className="card-title"
              onClick={() => console.log(`cliclk ${email}`)}
            >
              {user.name}
            </h4>
            <p className="card-text">{email}</p>
            <p>{tempPath}</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PopUp;
