import React, { useState, useCallback } from "react";
import axios from "axios";

const UploadArray = () => {
  const [imgCollections, setImageCollections] = useState({});

  const handleChange = useCallback((e) => {
    setImageCollections(e.target.files);
  }, []);

  const handleSubmit = useCallback(
    (e) => {
      e.preventDefault();
      const data = new FormData();

      for (const keys of Object.keys(imgCollections)) {
        data.append("imgCollection", imgCollections[keys]);
      }
      const config = {
        headers: {
          "content-type": "multipart/form-data",
        },
      };

      axios
        .post("http://localhost:8000/blogs/image-upload-array", data, config)
        .then((res) => {
          console.log("IMAGE UPLOAD SUCESS", res);
        })
        .catch((err) => {
          if (err) {
            console.log(`IMAGE UPLOAD ERROR ${err}`);
          }
        });
    },
    [imgCollections]
  );

  return (
    <div className="container">
      <div className="row">
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <input
              type="file"
              name="imgCollection"
              onChange={handleChange}
              multiple
            />
            <div className="form-group">
              {" "}
              <button className="btn btn-primary" type="submit">
                upload Array
              </button>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
};

export default UploadArray;
