import React, { useEffect, useState, useCallback } from "react";
import axios from "axios";
import { useSelector } from "react-redux";

const Fullpage = ({ match }) => {
  let path = "";
  let [article, setArticle] = useState([]);

  const [keys, setKeys] = useState({
    author: "",
    cusineImagesPath: [],
    cusineImagesDescription: [],
    visitedPlacesImagesPath: [],
    visitedPlacesImagesDescription: [],
    title: "",
    description: "",
    markdown: "",
    imagePath: "",
    postedByEmail: "",
  });

  const id = match.params.userToken;

  useEffect(() => {
    axios
      .post("http://localhost:8000/blogs/get-one-article", { id })
      .then((res) => {
        console.log("fetching posts from server SUCCESS", res);
        setArticle(res.data.result);
        setKeys({
          ...keys,
          author: res.data.result.author,
          cusineImagesPath: res.data.result.cusineImagesPath,
          cusineImagesDescription: res.data.result.cusineImagesDescription,
          visitedPlacesImagesPath: res.data.result.visitedPlacesImagesPath,
          visitedPlacesImagesDescription:
            res.data.result.visitedPlacesImagesDescription,
          title: res.data.result.title,
          description: res.data.result.description,
          markdown: res.data.result.markdown,
          imagePath: res.data.result.imagePath,
          postedByEmail: res.data.result.postedByEmail,
        });
      })
      .catch((err) => {
        if (err) {
          console.log("Error in fetching from database", err);
        }
      });
  }, []);

  const {
    author,
    cusineImagesPath,
    cusineImagesDescription,
    visitedPlacesImagesPath,
    visitedPlacesImagesDescriptio,
    title,
    description,
    markdown,
    imagePath,
    postedByEmail,
  } = keys;
  path =
    "http://localhost:8000/uploads/" + String(article.imagePath).substr(15);

  return (
    <div className="custom-top-background-blog">
      <div className="grid-container">
        <div className="item1">
          <div className="col md-8">
            {" "}
            <img src={path} className="img-responsive" />
          </div>
          <div className="col-md-4">This is col 2</div>
        </div>

        <div className="item2 align-items-center custom-blog-background-card">
          {visitedPlacesImagesPath.map((eachPath, index) => (
            <div key={index} className="col-md-4">
              <div className="card ">
                {" "}
                <img
                  id="imgCard"
                  className="card-img-top img-fluid"
                  src={eachPath}
                />{" "}
                <div className="card-body">
                  {" "}
                  {article.visitedPlacesImagesDescription[index]}{" "}
                </div>
              </div>{" "}
            </div>
          ))}
        </div>
        <div className="col">One of three columns</div>
        <div className="col">One of three columns</div>
        <div className="col">One of three columns</div>

        <div className="row align-items-end">
          <div className="col">One of three columns</div>
          <div className="col">One of three columns</div>
          <div className="col">One of three columns</div>
        </div>
      </div>
    </div>
  );
};

export default Fullpage;
