import React, { useEffect, useState } from "react";
import axios from "axios";
import { useSelector } from "react-redux";

const Fullpage = ({ match }) => {
  let [path, setPath] = useState("");
  let article = {};
  //let [article, setArticle] = useState({});
  const id = match.params.userToken;

  useEffect(() => {
    axios
      .post("http://localhost:8000/blogs/get-one-article", { id })
      .then((res) => {
        console.log("fetching posts from server SUCCESS", res);
        article = res.data.result;
        console.log(article);
        setPath(
          "http://localhost:8000/uploads/" + article.imagePath.substr(15)
        );
        console.log(`path is ${path}`);
      })
      .catch((err) => {
        if (err) {
          console.log("Error in fetching from database", err);
        }
      });
  }, []);

  return (
    <div className="custom-top-background-blog">
      <div>
        <div>
          <div className="row">
            <img className="img-fluid" src={path} />
            <h1>{path}</h1>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Fullpage;
