import React, { Fragment } from "react";
import { Link, withRouter } from "react-router-dom";
import { isAuth, signout } from "../utils/helper";
import { useDispatch } from "react-redux";
import { DropdownButton, Dropdown } from "react-bootstrap";

const Layout = ({ children, match, history }) => {
  const dispatch = useDispatch();
  const isActive = (path) => {
    if (match.path === path) {
      return { color: "#F0E68C" };
    } else {
      return { color: "#00FFFF" };
    }
  };

  const nav = () => (
    <nav className="navbar navbar-inverse custom-navbar">
      <div className="container-fluid">
        <div className="navbar-header">
          <a className="navbar-brand" href="/">
            Traveller
          </a>
        </div>

        {!isAuth() && (
          <>
            <ul className="nav navbar-nav">
              <li style={isActive("/signup")}>
                <a href="/signup"> Signup</a>
              </li>
            </ul>
          </>
        )}

        {isAuth() && (
          <>
            <li style={isActive("/user/dashboard")}>
              <a href="/user/dashboard"> Dashboard</a>
            </li>

            <ul className="navbar-nav ml-auto">
              <li
                style={{ cursor: "pointer", color: "#fff" }}
                onClick={() => {
                  signout(() => {
                    history.push("/");
                    localStorage.clear();
                  });
                }}
              >
                Signout
              </li>

              <DropdownButton title="View Profile">
                <Dropdown.Item href="/user/posts">My Posts</Dropdown.Item>
                <Dropdown.Item href="/user/update-profile">
                  Profile
                </Dropdown.Item>
              </DropdownButton>
            </ul>
          </>
        )}
      </div>
    </nav>
  );

  return (
    <Fragment>
      {nav()}
      <div className="container">{children}</div>
    </Fragment>
  );
};

export default withRouter(Layout);
