import React, { useState, useEffect } from "react";
import axios from "axios";
import Layout from "./Layout";
import { Link } from "react-router-dom";
//import PopUp from "./PopUp";

const Userpage = () => {
  const [listPosts, setPosts] = useState([]);
  const [displayPopup, setdisplaypopup] = useState(false);
  const [displayclass, setDisplayClass] = useState(true);

  useEffect(() => {
    axios
      .get("http://localhost:8000/blogs/all-posts")
      .then((res) => {
        console.log("fetching posts from server SUCCESS", res);

        setPosts(res.data.result);
        console.log(listPosts);
      })
      .catch((err) => {
        console.log("Error in fetching from database", err);
      });
  }, []);

  // const togglePopup = () => {
  //   setdisplaypopup(!displayPopup);
  // };

  const openPopup = (name, email) => (event) => {
    alert(`checking${name} and ${email}`);
    setDisplayClass(true);
    const handlecloseOfPOpup = (event) => {
      event.preventDefault();
      setDisplayClass(false);
    };

    return (
      displayclass && (
        <div>
          <div className="card custom-modal">
            <span className="custom-close" onClick={handlecloseOfPOpup}>
              &times;
            </span>
            <div className="card-body">
              <div className="card-title">{name}</div>
              <div className="card-text">{email}</div>
            </div>
          </div>
        </div>
      )
    );
  };

  return (
    <Layout>
      <h1>Blog Posts</h1>
      <div className="nav">
        {" "}
        <li>
          <Link to="/add-new-article" className="navbar-left">
            Add New Article
          </Link>{" "}
        </li>
      </div>
      <div>
        {listPosts.map((post) => {
          const path =
            "http://localhost:8000/uploads/" + post.imagePath.substr(15);
          return (
            <div className="card mt-4 custom-article-form" key={post._id}>
              <div className="row no-gutters">
                <div className="col-md-4">
                  <img src={path} className="card-img img-fluid" alt="lol" />
                </div>
                <div className="col-md-8">
                  <div className="card-title">{post.title}</div>
                  <div className="card-body">
                    {post.description}
                    <p>{post.markdown}</p>
                    <p>
                      posted by:{" "}
                      <button
                        onClick={openPopup(post.author, post.postedByEmail)}
                      >
                        {post.author}
                      </button>
                    </p>
                  </div>
                </div>
              </div>
            </div>
          );
        })}
      </div>
    </Layout>
  );
};

export default Userpage;
