import React, { useEffect, useState } from "react";
import Axios from "axios";

const PopUp = (props) => {
  const [user, setUser] = useState([]);
  const email = props.useremail;
  useEffect(() => {
    Axios.post("http://localhost:8000/blogs/get-user-details", {
      email,
    })
      .then((res) => {
        console.log("Success in Fetching user detail", email, res);
        setUser(res.data.result);
      })
      .catch((err) => {
        if (err) {
          console.log(
            "Error in fetching user details from the user",
            email,
            err
          );
        }
      });
  }, []);

  const handleClick = () => {
    props.toggle();
  };

  return (
    <div className="custom-modal">
      <div className="custom-modal-content">
        <span className="custom-close" onClick={handleClick}>
          &times;
        </span>
        <div className="card">
          <img className="card-img-top" alt="Card image" />
          <div className="card-body">
            <h4 className="card-title">{user.name}</h4>
            <p className="card-text">{email}</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PopUp;
